.about-partners{--gap:28px;--card-bg:#fff;--card-radius:18px;--shadow:0 4px 12px rgba(0,0,0,.06),0 1px 2px rgba(0,0,0,.05);margin:42px 0 60px;padding:0}
.about-partners h2,.about-partners h4{font-weight:600;letter-spacing:.5px;display:flex;align-items:center;gap:.6rem}
.about-partners h2{font-size:2.1rem;margin:0 0 10px}
.about-partners h4{font-size:1.05rem;margin:0 0 6px;line-height:1.5;font-weight:500}
.about-partners .dot{width:10px;height:10px;border-radius:50%;background:linear-gradient(135deg,#6366f1,#8b5cf6);display:inline-block;box-shadow:0 0 0 4px rgba(99,102,241,.15)}
.partner-grid{display:grid;grid-template-columns:repeat(auto-fill,minmax(220px,1fr));gap:var(--gap);margin-top:28px}
.partner-card{position:relative;list-style:none;background:var(--card-bg);border-radius:var(--card-radius);box-shadow:var(--shadow);padding:22px 18px 26px;display:flex;flex-direction:column;align-items:center;justify-content:flex-start;overflow:hidden;transition:.35s cubic-bezier(.4,0,.2,1);border:1px solid #f1f3f7}
.partner-card:before{content:"";position:absolute;inset:0;background:linear-gradient(135deg,rgba(99,102,241,.08),rgba(236,72,153,.08));opacity:0;transition:.45s}
.partner-card:hover{transform:translateY(-6px);box-shadow:0 12px 28px -6px rgba(0,0,0,.18),0 4px 10px rgba(0,0,0,.08)}
.partner-card:hover:before{opacity:1}
.partner-avatar{width:160px;height:220px;border-radius:22px;background:#f3f5f9;object-fit:cover;object-position:center;box-shadow:0 6px 16px rgba(0,0,0,.12);border:4px solid #fff;transition:.45s}
.partner-card:hover .partner-avatar{transform:translateY(-4px) scale(1.03)}
.partner-name{margin:16px 0 6px;font-size:1.05rem;font-weight:600;color:#1f2937;letter-spacing:.5px;text-align:center;line-height:1.25}
.partner-name .cn{display:block;font-size:1.05rem}
.partner-name .en{display:inline-block;margin-top:4px;font-size:.72rem;letter-spacing:1.6px;text-transform:uppercase;padding:5px 10px;border-radius:32px;background:linear-gradient(90deg,#6366f1,#ec4899 65%,#f59e0b);color:#fff;font-weight:600;box-shadow:0 2px 6px rgba(0,0,0,.18)}
.partner-desc{font-size:.8rem;line-height:1.45;color:#4b5563;text-align:center;margin-top:10px;min-height:48px;max-width:200px}
.role-badges{display:flex;flex-wrap:wrap;gap:6px;justify-content:center;margin-top:12px}
.role-badges span{font-size:.62rem;padding:4px 7px;border-radius:6px;background:#eef2ff;color:#4338ca;font-weight:600;letter-spacing:.5px}
.section-intro{font-size:.95rem;color:#374151;line-height:1.7;margin-top:4px;max-width:860px}
.dark .partner-card{background:#1f2530;border-color:#2b3442}
.dark .partner-desc{color:#9ca3af}
.dark .partner-name{color:#f3f4f6}
.dark .role-badges span{background:#312e81;color:#c7d2fe}
@media (max-width:640px){.about-partners h2{font-size:1.7rem}.partner-avatar{width:140px;height:190px}.partner-card{padding:18px 16px}.partner-desc{font-size:.75rem}.partner-name .en{font-size:.68rem}}




