function extractPersonName(imgUrl){
  try {
    const file = imgUrl.split('/').pop().split('?')[0].split('#')[0];
    const base = file.replace(/\.png$/i,'').replace(/\.jpg$/i,'').replace(/\.jpeg$/i,'');
    // 简单首字母大写，其余保持
    return base.charAt(0).toUpperCase() + base.slice(1);
  } catch(e){
    return '';
  }
}

(function renderPartners(){
  const grid = document.getElementById('partnerGrid');
  if(!grid) return;
  grid.innerHTML = PARTNERS.map(p => {
    const tags = p.tags.map(t=>`${t}`).join('');
    const person = extractPersonName(p.img);
    const personLine = person ? `${person}` : '';
    return `\n      \n      ${p.cn}${p.en}${personLine}\n      ${p.desc}\n      ${tags}\n    `
  }).join('');
})();
